#!/bin/bash
#
# CryptoCurrency Exchange Rates
# 
# <bitbar.title>POWR</bitbar.title>
# <bitbar.version>v1.0</bitbar.version>

powr_icon=''
raw_spot_price=$(curl -s https://api.coinmarketcap.com/v1/ticker/power-ledger/\?convert\=AUD | /usr/local/bin/jq -r '.[].price_aud')
spot_price=$(printf "%0.2f\n" $raw_spot_price)

echo "$spot_price | color=black image=$powr_icon "
